                    casillero43.BackgroundImage = Plumber.Properties.Resources.Signo_Fondo_Boton;
                    casillero44.BackgroundImage = Plumber.Properties.Resources.Signo_Fondo_Boton;
                    casillero45.BackgroundImage = Plumber.Properties.Resources.Signo_Fondo_Boton;
                    tiempo.Text = "180 Segundos";
                    time = 180;
                    timer1.Start();
                }
                else
                {
                    if (mensaje == DialogResult.No)
                    {
                        ActiveForm.Close();
                    }
                }
            }
            else
            {
                time--;
                tiempo.Text = time.ToString() + " Segundos";
            }
        }

